        :rules="formRules"
        @keyup.enter="submitForm">

        <el-row :gutter="15">
          <el-col :span="24" :xs="24">
            <el-form-item prop="isActive" label="">
              <el-switch v-model="form.isActive" active-text="Active"/>
            </el-form-item>
