  const { apiUrl } = runtimeConfig.public;

  const store = useAuthStore();

  let body = null;
  const query = null;
  if (method === 'GET') {
    if (params) {
      const queryParams = qs.stringify(params);
      if (!path.includes('?')) {
        path = path + '?';
