      if (showNotify) {
        if (options.method === 'PATCH' && response.ok) {
          // notification.updated()
        }
        if (options.method === 'POST' && response.ok) {
          notification.created();
        }
        if (options.method === 'DELETE' && response.ok) {
          notification.deleted();
        }

        if (!response.ok) {
          notification.error();
        }
      }
